import React, { useState, useEffect } from 'react';
import "./Featured.css";
import axios from "axios";
import { FiArrowUpRight, FiArrowDown } from "react-icons/fi";
import { useNavigate } from 'react-router-dom';


const Featured = () => {
    const [data, setData] = useState(null); 
    const url = "https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=6&page=1&sparkline=false&locale=en";

    useEffect(() => {
        axios.get(url)
            .then((response) => {
                setData(response.data);
            })
            .catch((error) => {
                console.log(error);
            });
    }, []);
    const history = useHistory();

  const handleClick = () => {

    history.push('/detailedcons');
  };


    console.log(data);

    if (!data) return null;

    return (
        <div className="featured">
            {/* left */}
            <div className ="container">
            <div className="left">
                <h2>Explore top Crypto's like Bitcoin, Ethereum, and Dogecoin</h2>
                <p>See all available assets: CryptoCurrencies and NFT's</p>
                <button className="btn">See more coins</button>
            </div>
            {/* right */}
            <div className="right">
                {data.slice(0, 6).map((coin, index) => (
                    <div className="card" key={index}>
                        <div className="top">
                            <img src={coin.image} alt="coin"></img>
                        </div>
                        <div>
                            <h5>{coin.name}</h5>
                            <p>${coin.current_price.toLocaleString()}</p>
                        </div>
                        {coin.price_change_percentage_24h < 0 ? (
                            <span className="red">
                                <FiArrowDown className ="icon" />
                                {coin.price_change_percentage_24h.toFixed(2)}%
                            </span>
                        ) : (
                            <span className="green">
                                <FiArrowUpRight className="icon" />
                                {coin.price_change_percentage_24h.toFixed(2)}%
                            </span>
                        )}
                    </div>
                ))}
            </div>
        </div>
                </div>
    );
};

export default Featured;
