import React, { useState, useEffect } from 'react';
import "./Featured.css";
import axios from "axios";
import { FiArrowUpRight, FiArrowDown } from "react-icons/fi";
import btc from "../assets/btc-img.png";

const Featured = () => {
    const [data, setData] = useState(null); 
    const url = "https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=6&page=1&sparkline=false&locale=en";

    useEffect(() => {
        axios.get(url)
            .then((response) => {
                setData(response.data);
            })
            .catch((error) => {
                console.log(error);
            });
    }, []);

    console.log(data);

    if (!data) return null;

    return (
        <div className="featured">
            {/* left */}
            <div className="left">
                <h2>Explore top Crypto's like Bitcoin, Ethereum, and Doggcoin</h2>
                <p>See all available assets: CryptoCurrencies and NFT's</p>
                <button className="btn">See more coins</button>
            </div>
            {/* right */}
            <div className="right">
                <div className="top">
                    <img src={data[0].image} alt="coin"></img>
                </div>
                <div>
                    <h5>{data[0].name}</h5>
                    <p>${data[0].current_price.toLocaleString()}</p>
                </div>

                {data[0].price_change_percentage_24h < 0 ? (
                    <span className="red">
                        <FiArrowDown/>
                        {data[0].price_change_percentage_24h}%
                    </span>
                ) : (
                    <span className="green">
                        <FiArrowUpRight/>
                        {data[0].price_change_percentage_24h}%
                    </span>
                )}

                {/* <span><FiArrowUpRight/>2.5%</span> */}
            </div>
        </div>
    );
};

export default Featured;
