import React,{useState, useEffect} from 'react'
import "./Featured.css"
import axios from "axios"
import btc from "./assets/btc-img.png " 
import {FiArrowUpRight} from "react-icons/fi"
const Featured = () => {
    const [data, setData] = useState(null); 
    const url = "https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=6&page=1&sparkline=false&locale=en"
    useEffect(() => {
    axios.get(url).then((response) => {
        setData(response.data);
    }).catch((error) => {
        console.log(error);
    });
}, []);

    return (
    <div className="featured">
        {/* left */}
        <div className="left">
            <h2>Explore top Crypto's like Bitcoin, Ethereum, and Doggcoin</h2>
            <p>See all available assets: CryproCurrencies and NFT's</p>
            <button className ="btn">See  more coins</button>
        </div>
        {/* right */}
      <div className = "right">
        <div className="top">
            <img src = {btc} alt = "bitcoin"></img>
        </div>
        <div>
        <h5>Bitcoin</h5>
        <p> $50,000</p>
        </div>
        <span><FiArrowUpRight/>2.5%</span>
      </div>
    </div>
  )
}

export default Featured
